#!/bin/bash
## INSTALADOR DE SOCNETV
## FECHA DE CREACIÃ“N: 14 de junio de 2023

## VARIABLES

ROOTDIR=$(realpath $(dirname $0))
PROTEPATH="/opt/socnetv"

## FUNCIONES

function error() {
    echo "[ERROR] $@. F"
    exit 1
}

function check_root() {
    if [[ $(whoami) != root ]]; then
        echo "[MALAMENTE] No eres root"
        error
    fi
}

function desinstalar() {
	rm -r /home/"$SUDO_USER"/*/*rot*g* "/home/$SUDO_USER/.icons/app-icons/socnetv.png" /home/"$SUDO_USER"/.local/share/applications/protege.desktop "$PROTEPATH"
}

function deb_download(){
    LINK="https://api.github.com/repos/socnetv/app/releases/latest"
    FILENAME="socnetv.AppImage"
    DEBTEMP="/tmp/${FILENAME}"

    mkdir -p "$PROTEPATH"
    chown -R ${SUDO_USER}:${SUDO_USER} "$PROTEPATH"
    cd $(dirname "$DEBTEMP")
    curl -s $LINK |
        grep "browser_download_url.*AppImage" |
        cut -d : -f 2,3 |
        tr -d \" |
        wget -O "$DEBTEMP" -i -

    mv "$DEBTEMP" "$PROTEPATH"
    chmod +x "${PROTEPATH}/${FILENAME}"
    cat  > "/home/$SUDO_USER/.local/share/applications/protege.desktop" <<EOF
[Desktop Entry]
Version=1.0
Type=Application
Name=SocNetV
Comment=Visualizador virtual de redes de tipo social
Exec="${PROTEPATH}/${FILENAME}"
Icon=/home/$SUDO_USER/.icons/app-icons/socnetv.png
Path=
Terminal=false
StartupNotify=true
Categories=Development;Office;
EOF
    chown -R $SUDO_USER:$SUDO_USER "/home/$SUDO_USER/.local/share/applications/protege.desktop"
    mkdir -p "/home/$SUDO_USER/.icons/app-icons"
    echo "" | base64 --decode > "/home/$SUDO_USER/.icons/app-icons/socnetv.png"
}

## LLAMADAS

check_root
if [[ "$1" != "-d" ]]; then
	deb_download
else
	desinstalar
fi

exit 0
